INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2017-03-28 07:14:27', '2017-03-28 07:14:27', 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2017-03-28 15:50:04', '2017-03-28 15:50:04', '', 0, 'http://gameworld.local/?p=1', 0, 'post', '', 1),
(2, 1, '2017-03-28 07:14:27', '2017-03-28 07:14:27', 'This is an example page. It''s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I''m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin'' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href="http://gameworld.local/wp-admin/">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2017-03-28 07:14:27', '2017-03-28 07:14:27', '', 0, 'http://gameworld.local/?page_id=2', 0, 'page', '', 0),
(3, 1, '2017-03-28 07:14:36', '0000-00-00 00:00:00', '', 'Auto Draft', '', 'auto-draft', 'open', 'open', '', '', '', '', '2017-03-28 07:14:36', '0000-00-00 00:00:00', '', 0, 'http://gameworld.local/?p=3', 0, 'post', '', 0),
(4, 1, '2017-03-28 14:29:47', '2017-03-28 14:29:47', 'Really cool game ever!!!!\r\n\r\nSed ut perspiciatis unde omnis iste natus error sit voluptatem accusantium doloremque laudantium, totam rem aperiam, eaque ipsa quae ab illo inventore veritatis et quasi architecto beatae vitae dicta sunt explicabo.\r\n\r\nNeque porro quisquam est, qui dolorem ipsum quia dolor sit amet, consectetur, adipisci velit, sed quia non numquam eius modi tempora incidunt.', 'Cool Game Ever!!!', '', 'publish', 'open', 'open', '', 'cool-game-ever', '', '', '2017-03-29 06:20:16', '2017-03-29 06:20:16', '', 0, 'http://gameworld.local/?post_type=product&#038;p=4', 0, 'product', '', 0),
